import React from "react";

const TodoItem = ({ todo, removeTodo, toggleComplete, updateDueDate }) => {
  const { id, text, completed, due_date } = todo;

  const handleRemoveClick = () => {
    removeTodo(id);
  };

  const handleCheckboxChange = () => {
    toggleComplete(id);
  };

  const handleDateChange = (e) => {
  const newDate = e.target.value;
  if (newDate) {
    updateDueDate(id, newDate);
  }
};

  const isLate = new Date() > new Date(due_date) && !completed;

  const formatDate = (dateString) => {
  if (!dateString) {
    return ""; // Return empty string if the dateString is empty
  }

  const date = new Date(dateString);
  if (isNaN(date.getTime())) {
    return ""; // Return empty string if the date is invalid
  }

  const options = { year: "numeric", month: "2-digit", day: "2-digit" };
  const formattedDate = date.toLocaleDateString(undefined, options);
  return formattedDate;
};








  return (
    <li className={isLate ? "late" : ""}>
      <input
        type="checkbox"
        checked={completed}
        onChange={handleCheckboxChange}
      />
      <span style={{ textDecoration: completed ? "line-through" : "none" }}>
        {text}
      </span>
      <input
        type="date"
        value={formatDate(due_date )}
        onChange={handleDateChange}
        className="date-field"
      />
      <button onClick={handleRemoveClick}>Remove</button>
    </li>
  );
};

export default TodoItem;
