import React, { useState, useEffect } from "react";
import axios from "axios";
import TodoForm from "./Components/TodoForm";
import TodoList from "./Components/TodoList";
import './App.css';

const App = () => {
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState("all");
  const [editTaskId, setEditTaskId] = useState(null);
  const [updatedTaskText, setUpdatedTaskText] = useState("");
  const [updatedDueDate, setUpdatedDueDate] = useState("");

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      const response = await axios.get("/todos");
      setTodos(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const addTodo = async (text, dueDate) => {
    try {
      const response = await axios.post("/todos", {
        text,
        completed: false,
        due_date: dueDate
      });
      const newTodo = response.data;
      setTodos([...todos, newTodo]);
    } catch (error) {
      console.error(error);
    }
  };

  const removeTodo = async (id) => {
    try {
      await axios.delete(`/todos/${id}`);
      const updatedTodos = todos.filter((todo) => todo.id !== id);
      setTodos(updatedTodos);
    } catch (error) {
      console.error(error);
    }
  };

  const toggleComplete = async (id) => {
    try {
      const todo = todos.find((todo) => todo.id === id);
      const updatedTodo = { ...todo, completed: !todo.completed };
      await axios.put(`/todos/${id}`, updatedTodo);
      const updatedTodos = todos.map((todo) => {
        if (todo.id === id) {
          return updatedTodo;
        }
        return todo;
      });
      setTodos(updatedTodos);
    } catch (error) {
      console.error(error);
    }
  };

  const updateDueDate = async (id, newDate) => {
    try {
      const todo = todos.find((todo) => todo.id === id);
      const updatedTodo = { ...todo, due_date: newDate };
      await axios.put(`/todos/${id}`, updatedTodo);
      const updatedTodos = todos.map((todo) => {
        if (todo.id === id) {
          return updatedTodo;
        }
        return todo;
      });
      setTodos(updatedTodos);
    } catch (error) {
      console.error(error);
    }
  };

  const handleEdit = (id) => {
    setEditTaskId(id);
    const todo = todos.find((todo) => todo.id === id);
    setUpdatedTaskText(todo ? todo.text : "");
    setUpdatedDueDate(todo ? todo.due_date : "");
  };

  const handleTaskUpdate = (e) => {
    setUpdatedTaskText(e.target.value);
  };

  const handleDueDateUpdate = (e) => {
    setUpdatedDueDate(e.target.value);
  };

  const handleTaskUpdateSubmit = async () => {
    if (editTaskId) {
      try {
        const todo = todos.find((todo) => todo.id === editTaskId);
        const updatedTodo = {
          ...todo,
          text: updatedTaskText,
          due_date: updatedDueDate
        };
        await axios.put(`/todos/${editTaskId}`, updatedTodo);
        const updatedTodos = todos.map((todo) => {
          if (todo.id === editTaskId) {
            return updatedTodo;
          }
          return todo;
        });
        setTodos(updatedTodos);
        setEditTaskId(null);
        setUpdatedTaskText("");
        setUpdatedDueDate("");
      } catch (error) {
        console.error(error);
      }
    }
  };

  const handleFilterChange = (status) => {
    setFilter(status);
  };

  // Apply filter based on status
  const filteredTodos = filter === "all"
    ? todos
    : todos.filter(todo => todo.completed === (filter === "completed"));

  return (
    <div className="container">
      <TodoForm addTodo={addTodo} setFilter={handleFilterChange} />
      <TodoList
        todos={filteredTodos}
        toggleComplete={toggleComplete}
        removeTodo={removeTodo}
        updateDueDate={updateDueDate}
        handleEdit={handleEdit}
        editTaskId={editTaskId}
        updatedTaskText={updatedTaskText}
        updatedDueDate={updatedDueDate}
        handleTaskUpdate={handleTaskUpdate}
        handleDueDateUpdate={handleDueDateUpdate}
        handleTaskUpdateSubmit={handleTaskUpdateSubmit}
      />
    </div>
  );
};

export default App;
