import React, { useState } from "react";

const TodoList = ({
  todos,
  toggleComplete,
  removeTodo,
  updateDueDate,
  handleEdit,
  editTaskId,
  updatedTaskText,
  updatedDueDate,
  handleTaskUpdate,
  handleDueDateUpdate,
  handleTaskUpdateSubmit,
}) => {
  const [showEditPopup, setShowEditPopup] = useState(false);

  const openEditPopup = (id) => {
    const todo = todos.find((todo) => todo.id === id);
    handleEdit(id);
    handleTaskUpdate({ target: { value: todo.text } }); // Set the initial task text
    handleDueDateUpdate({ target: { value: todo.due_date } }); // Set the initial due date
    setShowEditPopup(true);
  };

  const closeEditPopup = () => {
    setShowEditPopup(false);
    handleEdit(null);
  };

  const handleUpdateTask = () => {
    if (editTaskId) {
      const updatedTodos = todos.map((todo) => {
        if (todo.id === editTaskId) {
          return {
            ...todo,
            text: updatedTaskText || todo.text, // Use the updated text or fallback to the original task text
            due_date: updatedDueDate, // Use the updated due date
          };
        }
        return todo;
      });
      // Update the todos with the modified task
      // Use the appropriate state setter function here
      setTodos(updatedTodos);
      setUpdatedTaskText(""); // Reset the updated task text
      setUpdatedDueDate(""); // Reset the updated due date
      handleEdit(null);
    }
  };

  return (
    <div className="todo-list">
      {todos.map((todo) => (
        <div className="todo-item" key={todo.id}>
          <div className="task-container">
            {todo.editing ? (
              <div className="edit-popup">
                <input
                  type="text"
                  className="edit-task-input"
                  value={updatedTaskText}
                  onChange={handleTaskUpdate}
                />
                <input
                  type="date"
                  className="edit-date-input"
                  value={updatedDueDate}
                  onChange={handleDueDateUpdate}
                />
                <button className="update-button" onClick={handleUpdateTask}>
                  Update Task
                </button>
                <button className="cancel-button" onClick={closeEditPopup}>
                  Cancel
                </button>
              </div>
            ) : (
              <div className={`todo-text ${todo.completed ? "completed" : ""}`}>
                {todo.text}
              </div>
            )}
          </div>
          <div className="button-container">
            <input
              type="checkbox"
              checked={todo.completed}
              onChange={() => toggleComplete(todo.id)}
            />
            <button
              className="edit-button"
              onClick={() => openEditPopup(todo.id)}
              disabled={showEditPopup || todo.editing}
            >
              Edit
            </button>
            <button className="remove-button" onClick={() => removeTodo(todo.id)}>
              Remove
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default TodoList;
