import React from "react";

const TodoItem = ({ todo, removeTodo, toggleComplete, updateDueDate }) => {
  const { id, text, completed, due_date } = todo;

  const handleRemoveClick = () => {
    removeTodo(id);
  };

  const handleCheckboxChange = () => {
    toggleComplete(id);
  };

  const handleDateChange = (e) => {
    const newDate = e.target.value;
    if (newDate) {
      updateDueDate(id, newDate);
    }
  };

  const isLate = new Date() > new Date(due_date) && !completed;

  const formatDate = (dateString) => {
    if (!dateString || dateString === "dd/mm/yyyy") {
      return ""; // Return empty string for empty or default date
    }

    const [year, month, day] = dateString.split("-"); // Split the dateString into year, month, and day
    const formattedDate = `${day}/${month}/${year}`; // Rearrange the date parts in the desired format
    return formattedDate;
  };

  return (
    <li className={isLate ? "late todo-item" : "todo-item"}>
      <div className="todo-item-text">
        <input
          type="checkbox"
          checked={completed}
          onChange={handleCheckboxChange}
        />
        <span style={{ textDecoration: completed ? "line-through" : "none" }}>
          {text}
        </span>
      </div>
      <div className="todo-item-date">
        <span>{formatDate(due_date)}</span>
      </div>
      <button className="remove-button" onClick={handleRemoveClick}>
        Remove
      </button>
    </li>
  );
};

export default TodoItem;
