import React, { useState } from "react";

const TodoList = ({
  todos,
  toggleComplete,
  removeTodo,
  updateDueDate,
  handleEdit,
}) => {
  const [showEditPopup, setShowEditPopup] = useState(false);
  const [updatedTaskText, setUpdatedTaskText] = useState("");
  const [updatedDueDate, setUpdatedDueDate] = useState("");

  const handleTaskUpdate = (e) => {
    setUpdatedTaskText(e.target.value);
  };

  const handleDueDateUpdate = (e) => {
    setUpdatedDueDate(e.target.value);
  };

  const openEditPopup = (id) => {
  const todo = todos.find((todo) => todo.id === id);
  handleEdit(id);
  setShowEditPopup(true);
  setUpdatedTaskText(todo.text); // Set the current task text in the input bar
};


  const closeEditPopup = () => {
  setShowEditPopup(false);
  setUpdatedTaskText("");
  setUpdatedDueDate("");
};





  const handleUpdateTask = () => {
    // Call the update task function with the updated text and due date
    // Pass the task ID as well to identify which task to update
    // Example: updateTask(updatedTaskText, updatedDueDate, taskId);

    // Reset the state and close the popup
    setUpdatedTaskText("");
    setUpdatedDueDate("");
    setShowEditPopup(false);
  };

  return (
    <div className="todo-list">
      {todos.map((todo) => (
        <div className="todo-item" key={todo.id}>
          <div className="task-container">
            {todo.editing ? (
              <div className="edit-popup">
                <input
                  type="text"
                  className="edit-task-input"
                  value={updatedTaskText}
                  onChange={handleTaskUpdate}
                />
                <input
                  type="date"
                  className="edit-date-input"
                  value={updatedDueDate}
                  onChange={handleDueDateUpdate}
                />
                <button className="update-button" onClick={handleUpdateTask}>
                  Update Task
                </button>
                <button className="cancel-button" onClick={closeEditPopup}>
                  Cancel
                </button>
              </div>
            ) : (
              <div className={`todo-text ${todo.completed ? "completed" : ""}`}>
                {todo.text}
              </div>
            )}
          </div>
          <div className="button-container">
            <input
              type="checkbox"
              checked={todo.completed}
              onChange={() => toggleComplete(todo.id)}
            />
            <button
              className="edit-button"
              onClick={() => openEditPopup(todo.id)}
              disabled={showEditPopup || todo.editing}
            >
              Edit
            </button>
            <button className="remove-button" onClick={() => removeTodo(todo.id)}>
              Remove
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default TodoList;
