import React, { useState, useEffect } from "react";
import axios from "axios";
import TodoForm from "./Components/TodoForm";
import TodoList from "./Components/TodoList";
import './App.css';

const App = () => {
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState("all");
  const [editTaskId, setEditTaskId] = useState(null);
  const [updatedTaskText, setUpdatedTaskText] = useState("");
  const [updatedDueDate, setUpdatedDueDate] = useState("");

  const addTodo = (text, dueDate) => {
    const newTodo = {
      id: Date.now(),
      text,
      completed: false,
      due_date: dueDate
    };
    setTodos([...todos, newTodo]);
  };

  const removeTodo = (id) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  };

  const toggleComplete = (id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, completed: !todo.completed };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const updateDueDate = (id, newDate) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, due_date: newDate };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const handleEdit = (id) => {
  const updatedTodos = todos.map((todo) => {
    if (todo.id === id) {
      return { ...todo, editing: true };
    }
    return todo;
  });
  setTodos(updatedTodos);
  setEditTaskId(id);
  setUpdatedTaskText("");
  setUpdatedDueDate("");
};


  const handleTaskUpdate = (e) => {
    setUpdatedTaskText(e.target.value);
  };

  const handleDueDateUpdate = (e) => {
    setUpdatedDueDate(e.target.value);
  };

  const handleTaskUpdateSubmit = () => {
  if (editTaskId !== null && updatedTaskText !== "") {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === editTaskId) {
        return {
          ...todo,
          text: updatedTaskText,
          due_date: updatedDueDate !== "" ? updatedDueDate : todo.due_date,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
    setEditTaskId(null);
    setUpdatedTaskText("");
    setUpdatedDueDate("");
  }
};




  const handleFilterChange = (status) => {
    setFilter(status);
  };

  // Apply filter based on status
  const filteredTodos = filter === "all"
    ? todos
    : todos.filter(todo => todo.completed === (filter === "completed"));

  return (
    <div className="container">
      <TodoForm addTodo={addTodo} setFilter={handleFilterChange} />
      <TodoList
        todos={filteredTodos}
        toggleComplete={toggleComplete}
        removeTodo={removeTodo}
        updateDueDate={updateDueDate}
        handleEdit={handleEdit}
        editTaskId={editTaskId}
        updatedTaskText={updatedTaskText}
        updatedDueDate={updatedDueDate}
        handleTaskUpdate={handleTaskUpdate}
        handleDueDateUpdate={handleDueDateUpdate}
        handleTaskUpdateSubmit={handleTaskUpdateSubmit}
      />
    </div>
  );
};

export default App;
