// TodoList.js
import React from "react";

const TodoList = ({
  todos,
  toggleComplete,
  removeTodo,
  updateDueDate,
  handleEdit,
  editTaskId,
  updatedTaskText,
  updatedDueDate,
  handleTaskUpdate,
  handleDueDateUpdate,
  handleTaskUpdateSubmit,
  setEditTaskId,
  setUpdatedTaskText,
  setUpdatedDueDate,
  setTodos
}) => {
  return (
    <ul className="todo-list">
      {todos.map((todo) => (
        <li
          key={todo.id}
          className={`todo-item ${todo.due_date < new Date().toISOString().split('T')[0] ? 'task-past-due' : ''} ${editTaskId === todo.id ? 'editing' : ''}`}
        >
          {editTaskId !== todo.id ? (
            <div className="view">
              <label style={{ textDecoration: todo.completed ? "line-through" : "none" }}>
                {todo.text}
              </label>
              <input
                className="toggle"
                type="checkbox"
                checked={todo.completed}
                onChange={() => toggleComplete(todo.id)}
              />
              <button
                className="edit-button"
                onClick={() => handleEdit(todo.id)}
              >
                Edit
              </button>
              <button
                className="remove-button"
                onClick={() => removeTodo(todo.id)}
              >
                Remove
              </button>
            </div>
          ) : (
            <div className="edit-popup">
              <input
                type="text"
                value={updatedTaskText}
                onChange={handleTaskUpdate}
              />
              <input
                type="date"
                value={updatedDueDate}
                onChange={handleDueDateUpdate}
              />
              <button className="update-task-button" onClick={handleTaskUpdateSubmit}>Update Task</button>
              <button className="cancel-button" onClick={() => setEditTaskId(null)}>Cancel</button>
            </div>
          )}
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
